import React, {useState} from "react";
import NotesOverview from "./renderAddNote";


const Educational = (props) => {

  const eduEditTrueOrFalse = props.eduEditTrueOrFalse 

    return <>
            <form className="educational" onSubmit={props.test}>
            <label htmlFor="schoolInput">Enter school name</label>
            <input
              onChange={props.school}
              value={props.schoolValue}
              type="text"
              id="schoolInput"
            />
            <label htmlFor="studyInput">Enter title of study</label>
            <input
              onChange={props.study}
              value={props.studyValue}
              type="text"
              id="studyInput"            
            />
            <label htmlFor="dateInput">Enter date of study</label>
            <input
              onChange={props.date}
              value={props.dateValue}
              type="text"
              id="dateInput"            
            />
            <NotesOverview notes={props.notes} handleNoteChange={props.handleNoteChange} />
            <button type="button" onClick={props.addNote} className='middleButton'>add note</button>
            <button type="button" onClick={props.deleteNote} className='middleButton'>delete note</button>
            {eduEditTrueOrFalse ? <button type="button" onClick={props.handleEditChanges} className='middleButton'>Save Edit</button> : <button onClick={props.submit} type="button" >Save Changes</button>}




        </form>
    </>
}

export default Educational;
