                        <div className="personaliDiv"><FontAwesomeIcon icon={faPhone} /> <b>Telefoon:</b> <br></br>{personal.phone} <br></br></div>
                        <div className="personaliDiv"><FontAwesomeIcon icon={faEnvelope} /><b>E-mail:</b> <br></br>{personal.email} <br></br></div>
                        <div className="personaliDiv"><FontAwesomeIcon icon={faCalendar} /><b>Geboortedatum:</b> <br></br> {personal.birthDate} <br></br></div>
                        <div className="personaliDiv"><FontAwesomeIcon icon={faCar} /><b>Rijbewijs:</b> <br></br> {personal.car} <br></br> </div>
                    </div>

                    <div className="vaardigheden">
                        <h1 className="header" >Vaardigheden
                        <div className="line1"> </div>
                        <div className="line2"> </div> 
                        </h1>
                        <div className="vaardighedenDiv">
                            <b>{vaardigheden.een}</b> <br></br>
                            <Rating ratingvalue={vaardighedenRating.een} />  
                        </div>
                        <div className="vaardighedenDiv">
                            <b>{vaardigheden.twee}</b> <br></br>
                            <Rating ratingvalue={vaardighedenRating.twee} />
                        </div>
                        <div className="vaardighedenDiv">
                            <b>{vaardigheden.drie}</b> <br></br>
                            <Rating ratingvalue={vaardighedenRating.drie}/>
                        </div>
                        <div className="vaardighedenDiv">
                            <b>{vaardigheden.vier}</b> <br></br>
                            <Rating ratingvalue={vaardighedenRating.vier}/>
                        </div>
                        <div className="vaardighedenDiv">
                            <b>{vaardigheden.vijf}</b> <br></br>
                            <Rating ratingvalue={vaardighedenRating.vijf}/>
                        </div>
                    </div>

                    <div className="talen">
