import React, {useState} from "react";
import uniqid from "uniqid";
import Educational from "./educational";
import Personal from "./personal";
import Render from "./pageRender";
import Overview from "./overview";

// setPosition({ ...position, x: 100 })

const Parent = () => {

    //personal
    const [personal, setPersonal] = useState({name:'', email:'', phone:'', city:''})

    //educational
    const [eduTask, setEduTask] = useState({school: '', study: '', date: '', id: uniqid()})
    const [eduTasks, setEduTasks] = useState([])
    const [eduSelectedTask, setEduSelectedTask] = useState('')
    const [eduEditTrueOrFalse, setEduEditTrueOrFalse] = useState(false)

    //professional
    const [proTask, setProTask] = useState({company:'', position:'', mainResponsibilities:'', date:'', id: uniqid()})
    const [proTasks, setProTasks] = useState([])

    function personalName (data) {
        setPersonal
    }


    function school(data) {
        setEduTask({...eduTask, school: data.target.value})
    }

    function study(data) {
        setEduTask({...eduTask, study: data.target.value})
    }

    function date(data) {
        setEduTask({...eduTask, date: data.target.value})
    }

    const submit = (e) => {
        e.preventDefault();
        setEduTasks(eduTasks.concat(eduTask))
        setEduTask({school: '', study: '', date: '', id: uniqid()})
    }

    const handleRemove = (id) => {
        const newTasks = eduTasks.filter(element => element.id !== id)
        setEduTasks(newTasks)
    }

    const handleEdit = (id) => {
        const selectList = eduTasks.filter(element => {
            if(element.id === id) {
                const rightElement = {school: element.school, study: element.study, date: element.date, id: element.id};
                setEduTask(rightElement);
                setEduEditTrueOrFalse(true);
                setEduSelectedTask(id)
            }
        })
    }

    const handleEditChanges = (e) => {
        e.preventDefault(); // find the right array element, change it's content directly, and then use set... to change the state the correct way.
        const change = eduTasks.map(element => {
            if(element.id === eduSelectedTask) {
                element.school = eduTask.school
                element.study = eduTask.study
                element.date = eduTask.date
            }
            return element
        }) 
        setEduTasks(change)
        setEduTask({school: '', study: '', date: '', id: uniqid()})
        setEduSelectedTask('')
        setEduEditTrueOrFalse(false)
    }

    const consoleTest = (e) => {
        e.preventDefault()
        console.log(eduTask)
    }


    return <>
    <div className="appInside">
        <Personal personalInfo={personal} />
        <Educational 
        school={school} 
        schoolValue={eduTask.school}
        study={study}
        studyValue={eduTask.study}
        date={date}     
        dateValue={eduTask.date}
        submit={ (e) => submit(e) }
        eduEditTrueOrFalse={eduEditTrueOrFalse}
        handleEditChanges={handleEditChanges}
        console={consoleTest}
        />
                    <div className="render">
                <div className="personalSegment">{personal.name} <br></br> {personal.email} <br></br> {personal.phone} <br></br> {personal.city} </div>
                <div className="educationalSegment"><Overview tasks={eduTasks} handleRemove={handleRemove} handleEdit={handleEdit}/></div>
                <div className="professionalSegment">professional</div>
            </div>


    </div>
    </>

}

export default Parent;
