import React, {useState} from "react";

const Professional = (props) => {

    const proEditTrueOrFalse = props.proEditTrueOrFalse

    return<>
        <form className="professional" >
            <label htmlFor="companyInput">Enter company name</label>
            <input
              onChange={props.company}
              value={props.companyValue}
              type="text"
              id="companyInput"
            />
            <label htmlFor="positionInput">Enter position title</label>
            <input
              onChange={props.position}
              value={props.positionValue}
              type="text"
              id="positionInput"            
            />
            <label htmlFor="mainResponsibilitiesInput">Enter the main tasks of your role</label>
            <input
              onChange={props.mainResponsibilities}
              value={props.mainResponsibilitiesValue}
              type="text"
              id="mainResponsibilitiesInput"            
            />
            <label htmlFor="dateInput">Enter start date and end date of job</label>
            <input
              onChange={props.date}
              value={props.dateValue}
              type="text"
              id="dateInput"            
            />            
            {proEditTrueOrFalse ? <button type="button" onClick={props.handleEditChanges} >edit</button> :<button type="button" onClick={props.submit} >Save changes</button>}
        </form>
    </>
}

export default Professional
