import React, {useState} from "react";


const NotesOverview = (props) => {
    const notes = props.notes
    const handleNoteChange = props.handleNoteChange

    const updatedNotes = notes.map((note) => {

        const indexNumber = notes.findIndex(element => element.id === note.id)
        return <div>
            <label htmlFor="noteInput" >note:</label>
            <input
            onChange={handleNoteChange(indexNumber)}
            value={notes[indexNumber]}
            type='text'
            id='noteInput'
            />
        </div>
    }) 

    return (
        <div>
            {updatedNotes}
        </div>
    )
}






export default NotesOverview;
