import React, {useState} from "react";


const Educational = (props) => {

  const eduEditTrueOrFalse = props.passEdit
  const [buttonChange, setButtonChange] = useState(eduEditTrueOrFalse)

    return <>
            <form className="educational" onSubmit={props.test}>
            <label htmlFor="schoolInput">Enter school name</label>
            <input
              onChange={props.school}
              value={props.schoolValue}
              type="text"
              id="schoolInput"
            />
            <label htmlFor="studyInput">Enter title of study</label>
            <input
              onChange={props.study}
              value={props.studyValue}
              type="text"
              id="studyInput"            
            />
            <label htmlFor="dateInput">Enter date of study</label>
            <input
              onChange={props.date}
              value={props.dateValue}
              type="text"
              id="dateInput"            
            />
            <h1>{eduEditTrueOrFalse}</h1>
            {(eduEditTrueOrFalse) ? <button type="button">Save Edit</button> : <button onClick={props.submit} type="button" >Save Changes</button>}



        </form>
    </>
}

export default Educational;
