        />
                    <div className="render">
                <div className="personalSegment">personal </div>
                <div className="educationalSegment"><Overview tasks={eduTasks} /></div>
                <div className="professionalSegment">professional</div>
            </div>


    </div>
    </>

}

export default Parent;
