import React from "react";
import uniqid from "uniqid";

const Overview = (props) => {
    const tasks = props.tasks
    const handleRemove = props.handleRemove
    const handleEdit = props.handleEdit


    const updatedTasks = tasks.map((task) => {

        function NoteList(props) {
            const list = props.list;
            const updatedList = list.map((noteItems) => {
                return <li key={uniqid()} >{noteItems}</li>

            })
            return <ul>{updatedList}</ul>
        }

        return <div key={task.id} className={"segment"} >  
        <div className="headerOfDiv" > 
            <div><b>{task.school}</b></div> 
            <div><b>{task.study}</b></div>  
            <div><b>{task.date}</b></div> 
            <NoteList list={task.notes} />

        </div>
        <div className="buttons" >
            <button onClick={() => handleRemove(task.id)} >delete</button> 
            <button onClick={() => handleEdit(task.id)} >edit</button>
        </div>
        </div>
    })

    return (
        <div>
            {updatedTasks}
        </div>
    )

};

export default Overview;
