import React from "react"

const OverviewPro = (props) => {

    const tasks = props.tasks
    const handleRemove = props.handleRemove
    const handleEdit = props.handleEdit



    const updatedTasks = tasks.map((task) => {

        function NoteList(props) {
            const notes = task.notes
            const noteItems = notes.map((element) => {
                return <li key={element.id}>{element.note}</li> 
            })
            return (
                <ul>
                    {noteItems}
                </ul>
            )
        }

        return <div className="segment">
            <div className="headerOfDiv">
            <div className="upperPartProfessional">
                <div className="innerDiv1">{task.company}</div>
                <div className="innerDiv2">{task.position}</div>
                <div className="innerDiv3">{task.date}</div>
            </div>
            <div><NoteList/></div>
            <div className="buttons">
                <button onClick={() => handleRemove(task.id)} >delete</button>
                <button onClick={() => handleEdit(task.id)} >edit</button>
            </div>

        </div>
        </div>


    })

    return (
        <div>
            {updatedTasks}
        </div>
    )
}

export default OverviewPro
