import React, {useState} from "react";
import uniqid from "uniqid";
import Educational from "./educational";
import Render from "./pageRender";

// setPosition({ ...position, x: 100 })

const Parent = () => {

    //personal
    const [personal, setPersonal] = useState({name:'', email:'', phone:'', city:''})

    //educational
    const [eduTask, setEduTask] = useState({school: '', study: '', date: '', id: uniqid()})
    const [eduTasks, setEduTasks] = useState([])
    const [eduSelectedTask, setEduSelectedTask] = useState('')
    const [eduEditTrueOrFalse, setEduEditTrueOrFalse] = useState(false)

    //professional
    const [proTask, setProTask] = useState({company:'', position:'', mainResponsibilities:'', date:'', id: uniqid()})
    const [proTasks, setProTasks] = useState([])


    function school(data) {
        setEduTask({...eduTask, school: data.target.value})
    }

    function study(data) {
        setEduTask({...eduTask, study: data.target.value})
    }

    function date(data) {
        setEduTask({...eduTask, date: data.target.value})
    }

    const test = (e) => {
        e.preventDefault();
        setEduTasks(eduTasks.concat(eduTask))
        setEduTask({school: '', study: '', date: '', id: uniqid()})
    }

    const consoleTest = (e) => {
        e.preventDefault()
        console.log(eduTasks)
    }

    return <>
    <div>
    <Educational 
    school={school} 
    schoolValue={eduTask.school}
    study={study}
    studyValue={eduTask.study}
    date={date}     
    dateValue={eduTask.date}
    test={ (e) => test(e) }
    consoleTest= {(e) => consoleTest(e)}
    />
    <Render/>
    </div>
    </>

}

export default Parent;
